package oops;

public class pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j,k;
		for(i=0;i<4;i++)
		{
			for(j=0;j<=8;j++)
			{
				if(j%4==0)
				{
				System.out.print("*");
				}
				else
				{
					System.out.print(0);
				}
			}
			
			System.out.println();
			
		}

	}

}
